    private void btn1verActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn1verActionPerformed
        // TODO add your handling code here:
        // RECUPERANDO LOS VALORES DE LOS OBJETOS LOGICOS
        int tamanio = Integer.parseInt(TXT1.getText()); 
        String palabra = TXT2.getText(); 
        //DECLARANDO UN ARRAY ESTATICO DIMENSIONADO POR EL USUARIO
        String [] listaPalabras = new String [tamanio];
        //llenando array
        for (int i = 0; i < tamanio; i++) {
            listaPalabras[i] = palabra +i; //escrito el array
            txtVerContenido.setText(listaPalabras[i]);
        }
        
    }//GEN-LAST:event_btn1verActionPerformed


